const s=JSON.parse('{"key":"v-661ff770","path":"/canvas/threejs/%E5%9F%BA%E6%9C%AC%E5%85%83%E7%B4%A0%E6%9D%90%E8%B4%A8.html","title":"材质","lang":"zh-CN","frontmatter":{"title":"材质","date":"2022-07-27T00:00:00.000Z","category":"canvas"},"headers":[{"level":2,"title":"MeshBasicMaterial","slug":"meshbasicmaterial","link":"#meshbasicmaterial","children":[]},{"level":2,"title":"MeshNormalMaterial","slug":"meshnormalmaterial","link":"#meshnormalmaterial","children":[]},{"level":2,"title":"LineBasicMaterial 线条材质","slug":"linebasicmaterial-线条材质","link":"#linebasicmaterial-线条材质","children":[]},{"level":2,"title":"添加光","slug":"添加光","link":"#添加光","children":[]},{"level":2,"title":"MeshLambertMaterial 兰伯特材质","slug":"meshlambertmaterial-兰伯特材质","link":"#meshlambertmaterial-兰伯特材质","children":[]},{"level":2,"title":"MeshPhongMaterial 高光材质","slug":"meshphongmaterial-高光材质","link":"#meshphongmaterial-高光材质","children":[]},{"level":2,"title":"MeshStandardMaterial 标准材质","slug":"meshstandardmaterial-标准材质","link":"#meshstandardmaterial-标准材质","children":[]},{"level":2,"title":"贴图","slug":"贴图","link":"#贴图","children":[]},{"level":2,"title":"环境贴图","slug":"环境贴图","link":"#环境贴图","children":[]}],"git":{},"readingTime":{"minutes":4.01,"words":1204},"filePathRelative":"canvas/threejs/基本元素材质.md","localizedDate":"2022年7月27日","excerpt":"<p>材质可以看成是材料和质感的结合。在渲染程式中，它是表面各可视属性的结合，这些可视属性是指表面的色彩、纹理、光滑度、透明度、反射率、折射率、发光度等。<code>Three.js</code>给我们封装好了大部分的材质效果。</p>\\n<h2> MeshBasicMaterial</h2>\\n<p>这种材质是一种简单的材质，不会受到光的影响，直接看到的效果就是整个物体的颜色都是一样，没有立体的感觉。<br>\\n<code>set</code>修改<code>color</code>，也可以赋值一个新的<code>THREE.Color</code>对象</p>\\n<div class=\\"language-javascript line-numbers-mode\\" data-ext=\\"js\\"><pre class=\\"shiki one-dark-pro\\" style=\\"background-color: #282c34\\" tabindex=\\"0\\"><code><span class=\\"line\\"><span style=\\"color: #C678DD\\">var</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #E06C75\\">material</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #56B6C2\\">=</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #C678DD\\">new</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #E5C07B\\">THREE</span><span style=\\"color: #ABB2BF\\">.</span><span style=\\"color: #61AFEF\\">MeshBasicMaterial</span><span style=\\"color: #ABB2BF\\">({ </span><span style=\\"color: #E06C75\\">color</span><span style=\\"color: #ABB2BF\\">: </span><span style=\\"color: #D19A66\\">0x00ffff</span><span style=\\"color: #ABB2BF\\"> }) </span><span style=\\"color: #7F848E; font-style: italic\\">//设置初始的颜色为浅蓝色</span></span>\\n<span class=\\"line\\"><span style=\\"color: #E5C07B\\">material</span><span style=\\"color: #ABB2BF\\">.</span><span style=\\"color: #E5C07B\\">color</span><span style=\\"color: #ABB2BF\\">.</span><span style=\\"color: #61AFEF\\">set</span><span style=\\"color: #ABB2BF\\">(</span><span style=\\"color: #D19A66\\">0xff00ff</span><span style=\\"color: #ABB2BF\\">)</span></span>\\n<span class=\\"line\\"><span style=\\"color: #E5C07B\\">material</span><span style=\\"color: #ABB2BF\\">.</span><span style=\\"color: #E06C75\\">color</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #56B6C2\\">=</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #C678DD\\">new</span><span style=\\"color: #ABB2BF\\"> </span><span style=\\"color: #E5C07B\\">THREE</span><span style=\\"color: #ABB2BF\\">.</span><span style=\\"color: #61AFEF\\">Color</span><span style=\\"color: #ABB2BF\\">(</span><span style=\\"color: #D19A66\\">0xff00ff</span><span style=\\"color: #ABB2BF\\">) </span><span style=\\"color: #7F848E; font-style: italic\\">//将颜色修改为紫色</span></span>\\n<span class=\\"line\\"></span></code></pre><div class=\\"line-numbers\\" aria-hidden=\\"true\\"><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div><div class=\\"line-number\\"></div></div></div>"}');export{s as data};
