<span class="line"><span style="color:#ABB2BF;">    </span><span style="color:#56B6C2;">&lt;</span><span style="color:#ABB2BF;">td</span><span style="color:#56B6C2;">&gt;&lt;</span><span style="color:#C678DD;">?</span><span style="color:#56B6C2;">=</span><span style="color:#E06C75;">$arr</span><span style="color:#ABB2BF;">[</span><span style="color:#98C379;">&#39;name&#39;</span><span style="color:#ABB2BF;">]</span><span style="color:#C678DD;">?</span><span style="color:#56B6C2;">&gt;&lt;/</span><span style="color:#ABB2BF;">td</span><span style="color:#56B6C2;">&gt;</span></span>
